# -*- coding:utf-8 -*-#작동 확인def fnum():		#첫번째 숫자를 입력받아 출력하며 입력값을 돌려줌	num =  int(input("첫번째 숫자를 입력하세요"))	print ("첫번째 숫자는",num,"입니다")	return num#작동확인def snum():        #두번째 숫자를 입력받아 출력하며 입력값을 돌려줌	num = int(input("두번째 숫자를 입력하세요"))	print ("두번째 숫자는",num,"입니다")	return num#작동확인def resetcheck():   	a=input("두번째 숫자를 입력하세요 만약 새로운 계산을 원하면 reset을 입력하세요")	if a == "reset":		return 0,0	else:		return 1,int(a)			def p_ans(a):	print(a)	return adef askcount(a,b):    ask = input("+,-,*,/ 중 원하는 연산을 입력하세요")    if ask == "+":        ans = a+b        return p_ans(ans)    elif ask == "-":        ans = a-b        return p_ans(ans)    elif ask == "*":        ans = a*b        return p_ans(ans)    elif ask == "/":        ans = a/b        return p_ans(ans)    else :        print("잘못 입력하셨습니다.")        askcount(a,b)                	     